package week1.day2;

public class Switchcase {

	public static void main(String[] args) {
		int value=2;
		switch(value) {
		case 1: System.out.println("Print ticket for balcony");
		break;
		case 2: System.out.println("Print ticket for 1st class");
		break;
		case 3: System.out.println("Print ticket for 2nd class");
		break;
		default: System.out.println("Please select 1,2,3");
		}
		// TODO Auto-generated method stub

	}

}
